import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import "../Styles/Home.css";
import BookByCity from "./BookByCity";
import axios from "axios";

const Home = () => {
  const [searchCity, setSearchCity] = useState("");
  const [selectedCity, setSelectedCity] = useState("");
  const [book, setBook] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  const handleSearch = async (e) => {
    e.preventDefault();

    if (!searchCity && !selectedCity) {
      setError("Please select or enter a city");
      return;
    }

    setLoading(true);
    setError(null);
    const city = searchCity || selectedCity;

    try {
      const response = await axios.get(`http://localhost:8080/cityResponse/${city}`);
      console.log("response-->>>>", response);
      setBook(response.data);
      navigate(`/book/cityResponse/${city}`);
    } catch (err) {
      setError("Failed to fetch books.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="home-container">
      {/* Hero Section with Carousel */}
      <div id="heroCarousel" className="carousel slide hero-carousel" data-bs-ride="carousel">
        <div className="carousel-inner">
          {[1, 2, 3].map((n, idx) => (
            <div
              key={n}
              className={`carousel-item ${idx === 0 ? 'active' : ''}`}
              style={{
                backgroundImage: `url(https://via.placeholder.com/1600x600?text=Banner+${n})`,
                backgroundSize: 'cover',
                backgroundPosition: 'center',
                height: '600px',
                position: 'relative'
              }}
            >
              <div className="overlay-content">
                <h1>
                  Find the best <span>Kitabay</span> to purchase and read..
                </h1>
                <form onSubmit={handleSearch} className="search-form">
                  <BookByCity />
                  <input
                    type="text"
                    placeholder="Enter city name..."
                    value={searchCity}
                    onChange={(e) => setSearchCity(e.target.value)}
                  />
                  <button type="submit">Search</button>
                </form>
              </div>
            </div>
          ))}
        </div>

        <button className="carousel-control-prev" type="button" data-bs-target="#heroCarousel" data-bs-slide="prev">
          <span className="carousel-control-prev-icon" />
          <span className="visually-hidden">Previous</span>
        </button>

        <button className="carousel-control-next" type="button" data-bs-target="#heroCarousel" data-bs-slide="next">
          <span className="carousel-control-next-icon" />
          <span className="visually-hidden">Next</span>
        </button>
      </div>

      {/* Explore Categories */}
      <div className="explore-section">
        <h2>Explore Books by its type</h2>
        <div className="category-list">
          <Link to="/book/booktype/Best Sellers">Best Sellers</Link>
          <Link to="/book/booktype/New Releases">New Release</Link>
          <Link to="/book/booktype/Spirituality">Spirituality</Link>
          <Link to="/book/booktype/Biographies">Biographies</Link>
        </div>
      </div>

      {/* Featured Books */}
      <div className="featured-section">
        <h2>Top Book Collections</h2>
        <div className="featured-list">
          <div className="featured-item">
            <img src="https://kitabay.com/cdn/shop/files/Copy_of_Decorate_your_home_with_Coffee_Table_Books_1.jpg?v=1727267160" alt="Book-1" />
            <p>Epic adventures to unknown pastures</p>
          </div>
          <div className="featured-item">
            <img src="https://kitabay.com/cdn/shop/files/Copy_of_Decorate_your_home_with_Coffee_Table_Books.jpg?v=1727267160" alt="Book-2" />
            <p>Dare to dream to believe</p>
          </div>
          <div className="featured-item">
            <img src="https://kitabay.com/cdn/shop/files/Copy_of_Decorate_your_home_with_Coffee_Table_Books_5.jpg?v=1738931005" alt="Book-3" />
            <p>Turn the page to new thrills</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Home;
