import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import '../Styles/Hindi-coll.css';

const HindiColl = () => {
  const { id } = useParams(); // Get the book ID from URL
  const [books, setBooks] = useState([]);
  const [book, setBook] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (id) {
      // Fetch a single book
      axios.get(`http://localhost:8080/coll/${id}`)
        .then(res => {
          setBook(res.data);
          setLoading(false);
        })
        .catch(err => {
          setError(err.message);
          setLoading(false);
        });
    } else {
      // Fetch all books
      axios.get("http://localhost:8080/coll")
        .then(res => {
          setBooks(res.data);
          setLoading(false);
        })
        .catch(err => {
          setError(err.message);
          setLoading(false);
        });
    }
  }, [id]);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error: {error}</p>;

  // ðŸ‘‡ If we're viewing a single book (from URL like /coll/1)
  if (id && book) {
    return (
      <div className="detail-container">
        <div className="left">
          <img src={book.image} alt={book.name} />
        </div>
        <div className="right">
          <h2>{book.name}</h2>
          <p>by <span className="author">{book.author || "Unknown Author"}</span></p>
          <p className="rating">â˜…â˜…â˜…â˜…â˜†</p>
          <p className="price-block">
            <span className="old">Rs. 150</span>
            <span className="new">Rs. {book.price}</span>
            <span className="save"> Save 3%</span>
          </p>
          <p>In Stock</p>
          <p>Binding: Paperback</p>
          <p>Condition: New</p>
          <div className="offers">
            <p>ðŸŽ‰ Grab 10% Off on Everything! Add any 3 or more items</p>
            <p>ðŸŽ‰ Grab 25% Off on Everything! Add any 6 or more items</p>
          </div>
          <button className="add-cart">Add to Cart</button>
          <button className="buy-now">Buy Now</button>
        </div>
      </div>
    );
  }

  // ðŸ‘‡ Otherwise, show all Hindi books
  return (
    <div className="allcoll">
      <h2>Hindi Book Collection</h2>
      <ul className="card-list">
        {books.map(book => (
          <li key={book.id}>
            <a href={`/coll/${book.id}`} className="css-coll">
              <img src={book.image} alt={book.name} />
              <strong>{book.name}</strong>
              <p className="price">Rs. {book.price}</p>
              <span className="original">Rs. 399</span>
              <button className="add-to-cart">Add to cart</button>
              <div className="stars">â˜…â˜…â˜…â˜…â˜†</div>
            </a>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default HindiColl;
