import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import "../Styles/Home.css";
import BookByCity from "./BookByCity";
import axios from "axios";

const Home = () => {
  const [searchCity, setSearchCity] = useState("");
  const [searchBook, setSearchBook] = useState("");
  const [selectedCity, setSelectedCity] = useState("");
  const [book, setBook] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const navigate=useNavigate();


  // Search book section//

  const handlebookSearch = async (e) => {
    e.preventDefault();

    if (!searchBook) {
      setError("Please enter book type");
      return;
    }

    setLoading(true);
    setError(null);
    const booktype = searchBook; 

    try {
      const response = await axios.get(`http://localhost:8080/bookType/${booktype}`);
      console.log("response-->>>>",response);
      setBook(response.data); 
      navigate(`/book/bookType/${booktype}`);
    } catch (err) {
      setError("Failed to fetch books.");
    } finally {
      setLoading(false);
    }
  };

  // city search section //

  const handleSearch = async (e) => {
    e.preventDefault();

    if (!searchCity && !selectedCity) {
      setError("Please select or enter a city");
      return;
    }

    setLoading(true);
    setError(null);
    const city = searchCity || selectedCity; // Use either the entered city or selected city

    try {
      const response = await axios.get(`http://localhost:8080/cityResponse/${city}`);
      console.log("response-->>>>",response);
      setBook(response.data); // Assuming the response contains the list of restaurants
      // Navigate to the results page with the city as a query parameter
      navigate(`/book/cityResponse/${city}`);
    } catch (err) {
      setError("Failed to fetch books.");
    } finally {
      setLoading(false);
    }
  };


  return (
    <div className="home-container">
      {/* Hero Section */}

      <div className="hero-section">
        <h1>Find the best <span>Kitabay</span> to purchase and read..</h1>
        <form onSubmit={handleSearch} className="search-form">
        <BookByCity/>
          <input
            type="text"
            placeholder="Enter city name..."
            value={searchCity}
            onChange={(e) => setSearchCity(e.target.value)}
          />
          <button type="submit">Search</button>
        </form>
      </div>

       <div className="hero-section-1">
          <input
            type="text"
            placeholder="Enter book..."
            value={searchBook}
            onChange={(e) => setSearchBook(e.target.value)}
          />
          <button type="submit">Search</button>
      
      </div>

      {/* Explore Categories */}
      <div className="explore-section">
        <h2>Explore Books by its type</h2>
        <div className="category-list">
          <Link to="/book/booktype/Best Sellers">Best Sellers</Link>
          <Link to="/book/booktype/New Releases">New Release</Link>
          <Link to="/book/booktype/Spirituality">Spirituality</Link>
          <Link to="/book/booktype/Biographies">Biographies</Link>
        </div>
      </div>

      {/* Featured Restaurants */}
      <div className="featured-section">
        <h2>Top Book Collections</h2>
        <div className="featured-list">
          <div className="featured-item">
            <img src="https://kitabay.com/cdn/shop/files/Copy_of_Decorate_your_home_with_Coffee_Table_Books.jpg?v=1727267160" alt="Book-1" />
            <p>Epic adventures to unknown pastures</p>
          </div>
          <div className="featured-item">
            <img src="https://kitabay.com/cdn/shop/files/Copy_of_Decorate_your_home_with_Coffee_Table_Books_1.jpg?v=1727267160" alt="Book-2" />
            <p>Dare to dream to believe</p>
          </div>
          <div className="featured-item">
            <img src="https://kitabay.com/cdn/shop/files/Copy_of_Decorate_your_home_with_Coffee_Table_Books_5.jpg?v=1738931005" alt="Book-3" />
            <p>Turn the page to new thrills </p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Home;
