import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";
import "../Styles/HinDetails.css"; // Make sure your CSS is correctly linked

const HinDetails = () => {
  const { id } = useParams();
  const navigate = useNavigate(); // ✅ moved inside component
  const [book, setBook] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    axios.get(`http://localhost:8080/coll/${id}`)
      .then((res) => {
        setBook(res.data);
        setLoading(false);
      })
      .catch(() => {
        setBook(null);
        setLoading(false);
      });
  }, [id]);

  if (loading) return <p>Loading...</p>;
  if (!book) return <p>Book not found</p>;

  return (
    <div className="book-details-container">
      <button className="back-btn" onClick={() => navigate(-1)}>← Back</button>
      <div className="book-details">
        <div className="book-image">
          <img src={book.image} alt={book.title || "Book Cover"} />
        </div>
        <div className="book-info">
          <h2>{book.title}</h2>
          <p><del>Rs. 499</del> <span className="price">Rs. 299</span></p>
          <p>Only 1 left in stock</p>
          <p>Binding: Paperback</p>
          <p>Condition: Gently Used</p>
          <button className="cart-btn">Add to cart</button>
          <button className="buy-btn">Buy now</button>
        </div>
      </div>
    </div>
  );
};

export default HinDetails;
