import { useLocation, useNavigate, useParams } from "react-router-dom";
import { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

function Results() {
  const location = useLocation();
  const queryParams = new URLSearchParams(location.search);
  const { city, booktype } = useParams(); // Get city and category from URL params
console.log(" city, bookType -->>>", city, booktype );
  const [book, setBook] = useState([]);
  const [error, setError] = useState(null);

  // Fetch restaurants based on city or category
  useEffect(() => {
    const fetchBook = async () => {
      try {
        let url = "";

        if (city) {

          url = `http://localhost:8080/cityResponse/${city}`;
        } else if (booktype) {

          url = `http://localhost:8080/bookType/${booktype}`;
        }

        if (url) {
          const response = await axios.get(url);
          console.log("Response data:", response.data);
          setBook(response.data.bookList || []); 
        }
      } catch (err) {
        setError("Failed to fetch books.");
      }
    };

    if (city || booktype) {
      fetchBook();
    }
  }, [city, booktype]);

  return (
    <div>
      <h1>
        Books in {city ? city : booktype ? booktype : "Our Collection"}
      </h1>

      {error && <p className="error">{error}</p>}

      <div className="restaurant-list">
        {book.length > 0 ? (
          book.map((book) => (
            <div key={book.id} className="restaurant-card">
              <Link to={`/book/${book.id}`} className="restaurant-link">
                <img
                  src={book.image || "/default-restaurant.jpg"} // Default image if not provided
                  alt={book.restaurant}
                  className="restaurant-image"
                />
                <div className="restaurant-details">
                  <h2>{book.book}</h2>
                  <p>üìç {book.city}</p>
                </div>
              </Link>
            </div>
          ))
        ) : (
          <p>No books found.</p>
        )}
      </div>
    </div>
  );
}

export default Results;
