import React, { useEffect, useState } from "react";
import axios from "axios";
import '../Styles/Hindi-coll.css';



const HindiColl = () => {
  const [coll, setHindiColl] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
   axios.get("http://localhost:8080/coll")
  .then((response) => {
    console.log("API Response:", response.data);

    if (response.data && response.data.collect) {
      setHindiColl([response.data.collect]); // Wrap it in array manually
    } else {
      setHindiColl([]); // No data fallback
    }

    setLoading(false);
  })
      .catch((error) => {
        console.error("Error fetching data:", error);
        setError(error.message);
        setLoading(false);
      });
  }, []);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error: {error}</p>;

  return (
    <div className="allcoll">
      <h2>Books</h2>
           {Array.isArray(coll) && coll.length > 0 ? (
             coll.map((coll) => (
               <li key={coll.lang} className="css-coll">
                <strong>
                <a href={`/coll/${coll.id}`}></a>
                </strong> - {coll.name}
              <p>
                See more books
              </p>
              </li>
              ))
        ) : (
          <p>No books available.</p>
        )}
      
      </div>

      
  );
};

export default HindiColl;
