import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

const Mystery = () => {
  const { mystery } = useParams(); // this could be a genre or location
  const [books, setBooks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    axios
      .get(`http://localhost:8080/Mys/${mystery}`) // adjust endpoint if needed
      .then((response) => {
        console.log("API Response:", response.data);

        const result = response.data?.Mys;
        if (Array.isArray(result)) {
          setBooks(result);
        } else {
          console.warn("Unexpected response shape", response.data);
          setBooks([]);
        }

        setLoading(false);
      })
      .catch((error) => {
        console.error("Error fetching mystery books:", error);
        setError(error.message);
        setLoading(false);
      });
  }, [mystery]);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error: {error}</p>;

  return (
    <div>
      <h2>Mystery Books {mystery && `in ${mystery}`}</h2>
      {books.length > 0 ? (
        <ul>
          {books.map((book) => (
            <li key={book._id}>
              <strong>{book.name}</strong> â€” {book.genre} ({book.city})
            </li>
          ))}
        </ul>
      ) : (
        <p>No mystery books found.</p>
      )}
    </div>
  );
};

export default Mystery;
