import React, { useEffect, useState } from "react";
import axios from "axios";
import '../Styles/Hindi-coll.css';



const HindiColl = () => {
  const [coll, setBook] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    axios
      .get("http://localhost:8080/coll")
      .then((response) => {
        console.log("API Response:", response.data); // Debugging
        if (Array.isArray(response.data)) {
          setBook(response.data);
        } else {
          console.error("Unexpected API response:", response.data);
          setBook([]); // Fallback to an empty array
        }
        setLoading(false);
      })
      .catch((error) => {
        console.error("Error fetching data:", error);
        setError(error.message);
        setLoading(false);
      });
  }, []);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error: {error}</p>;

  return (
    <div className="allcoll">
      <h2>Books</h2>
           {Array.isArray(coll) && book.length > 0 ? (
             book.map((coll) => (
               <li key={coll} className="css-coll">
                <strong>
                <a href={`/coll/${coll}`}>{coll.coll}</a>
                </strong> 
              <p>
                See more books
              </p>
              </li>
              ))
        ) : (
          <p>No books available.</p>
        )}
      
      </div>

      
  );
};

export default HindiColl;
