import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import "../Styles/Home.css";
import BookByCity from "./BookByCity";
import axios from "axios";
import { Swiper, SwiperSlide } from 'swiper/react';
import { Autoplay, Pagination } from 'swiper/modules';
import 'swiper/css';
import 'swiper/css/pagination';


const Home = () => {
  const [searchCity, setSearchCity] = useState("");
  const [selectedCity, setSelectedCity] = useState("");
  const [book, setBook] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const navigate=useNavigate();

  const handleSearch = async (e) => {
    e.preventDefault();

    if (!searchCity && !selectedCity) {
      setError("Please select or enter a city");
      return;
    }

    setLoading(true);
    setError(null);
    const city = searchCity || selectedCity; // Use either the entered city or selected city

    try {
      const response = await axios.get(`http://localhost:8080/cityResponse/${city}`);
      console.log("response-->>>>",response);
      setBook(response.data); // Assuming the response contains the list of restaurants
      // Navigate to the results page with the city as a query parameter
      navigate(`/book/cityResponse/${city}`);
    } catch (err) {
      setError("Failed to fetch books.");
    } finally {
      setLoading(false);
    }
  };


  return (
    <div className="home-container">
  

      <Swiper
  modules={[Pagination, Autoplay]}
  pagination={{ clickable: true }}
  autoplay={{ delay: 3000 }}
  loop={true}
  className="hero-carousel"
>
  <SwiperSlide>
    <div className="hero-section slide1">
      <h1>Find the best <span>Kitabay</span> to purchase and read..</h1>
      <form onSubmit={handleSearch} className="search-form">
        <BookByCity />
        <input
          type="text"
          placeholder="Enter city name..."
          value={searchCity}
          onChange={(e) => setSearchCity(e.target.value)}
        />
        <button type="submit">Search</button>
      </form>
    </div>
  </SwiperSlide>

  <SwiperSlide>
    <div className="hero-section slide2">
      <h1>Discover amazing <span>deals</span> on your favorite books!</h1>
      <form onSubmit={handleSearch} className="search-form">
        <BookByCity />
        <input
          type="text"
          placeholder="Search books by city..."
          value={searchCity}
          onChange={(e) => setSearchCity(e.target.value)}
        />
        <button type="submit">Search</button>
      </form>
    </div>
  </SwiperSlide>

  <SwiperSlide>
    <div className="hero-section slide3">
      <h1>Your next <span>great read</span> starts here!</h1>
      <form onSubmit={handleSearch} className="search-form">
        <BookByCity />
        <input
          type="text"
          placeholder="Where do you want your book?"
          value={searchCity}
          onChange={(e) => setSearchCity(e.target.value)}
        />
        <button type="submit">Search</button>
      </form>
    </div>
  </SwiperSlide>
</Swiper>


  
      <div className="featured-section">
        <h2>Top Book Collections</h2>
        <div className="featured-list">
          <div className="featured-item">
            <img src="https://kitabay.com/cdn/shop/files/Copy_of_Decorate_your_home_with_Coffee_Table_Books_1.jpg?v=1727267160" alt="Book-1" />
            
          </div>
          <div className="featured-item">
            <img src="https://kitabay.com/cdn/shop/files/Copy_of_Decorate_your_home_with_Coffee_Table_Books.jpg?v=1727267160" alt="Book-2" />
        
          </div>
          <div className="featured-item">
            <img src="https://kitabay.com/cdn/shop/files/Copy_of_Decorate_your_home_with_Coffee_Table_Books_5.jpg?v=1738931005" alt="Book-3" />
          
          </div>
        </div>
      </div>

      {/* Explore Categories */}
      <div className="explore-section">
        <h2>Good Reads</h2>
        <div className="category-list">
          <Link to="/book/booktype/Best Sellers">Best Sellers</Link><h1>|</h1>
          <Link to="/book/booktype/New Releases">New Release</Link><h1>|</h1>
          <Link to="/book/booktype/Spirituality">Spirituality</Link><h1>|</h1>
          <Link to="/book/booktype/Biographies">Biographies</Link><h1>|</h1>
          <Link to="/book/booktype/History">Historical</Link>
        </div>
      </div>

       <div className="book-card">
        <div className="book1">
          <Link to ="/book/1"><img src="https://kitabay.com/cdn/shop/files/a478156d6328ab3cd0810bca5e4f0336.jpg?crop=center&height=283&v=1753077394&width=178" alt="Book-1"/></Link>
         <strong>The Young Elites</strong>
          <p>
            <span className="original-price">Rs. 499</span>
            <span className="discounted-price">Rs. 299</span>
          </p>
          <span className="cart"> Add to cart </span>
        </div>
      </div> 
    
       <div className="book2">
          <Link to ="/book/2"><img src="https://kitabay.com/cdn/shop/files/998d5ba5722fd656a04b659e1032fcb2.jpg?crop=center&height=475&v=1752836848&width=315" alt="Book-2"/></Link>
         <strong>Allegiant</strong>
          <p>
            <span className="original-price2">Rs. 299</span>
            <span className="discounted-price2">Rs. 199</span>
          </p>
          <span className="cart2"> Add to cart </span>
        </div>  

        <div className="book3">
          <Link to ="/book/3"><img src="https://kitabay.com/cdn/shop/files/1c68518b03d5d4db7e593c8225849085.jpg?crop=center&height=798&v=1752835745&width=520" alt="Book-3"/></Link>
         <strong>The Swimming Pool</strong>
          <p>
            <span className="original-price3">Rs. 399</span>
            <span className="discounted-price3">Rs. 199</span>
          </p>
          <span className="cart3"> Add to cart </span>
        </div>  

        <div className="book4">
          <Link to ="/book/4"><img src="https://kitabay.com/cdn/shop/files/23350ccb7dd276dca443cafc3ca91263.jpg?crop=center&height=475&v=1752746308&width=309" alt="Book-4"/></Link>
         <strong>Five Days</strong>
          <p>
            <span className="original-price4">Rs. 599</span>
            <span className="discounted-price4">Rs. 299</span>
          </p>
          <span className="cart4"> Add to cart </span>
        </div>  

        <div className="book5">
          <Link to ="/book/5"><img src="https://kitabay.com/cdn/shop/files/3e5635dccdfe138e004733a931ebd616.jpg?crop=center&height=570&v=1752745485&width=370" alt="Book-5"/></Link>
         <strong>Bath Tangle</strong>
          <p>
            <span className="original-price5">Rs. 499</span>
            <span className="discounted-price5">Rs. 244</span>
          </p>
          <span className="cart5"> Add to cart </span>
        </div>    

        <div className="book6">
          <Link to ="/book/6"><img src="https://kitabay.com/cdn/shop/files/3e4a0fa053cec4c05d6fdc3307fc1677.jpg?crop=center&height=461&v=1753075946&width=300" alt="Book-6"/></Link>
         <strong>The Princetta</strong>
          <p>
            <span className="original-price6">Rs. 599</span>
            <span className="discounted-price6">Rs. 299</span>
          </p>
          <span className="cart6"> Add to cart </span>
        </div>    

        <div className="book7">
          <Link to ="/book/7"><img src="https://kitabay.com/cdn/shop/files/3bc764d8bddb593ff2c7df16a22c3523.jpg?crop=center&height=400&v=1753077591&width=264" alt="Book-7"/></Link>
         <strong>Tuscan Rose</strong>
          <p>
            <span className="original-price7">Rs. 399</span>
            <span className="discounted-price7">Rs. 199</span>
          </p>
          <span className="cart7"> Add to cart </span>
        </div>     
    </div>
  );
};

export default Home;




