import React from 'react';
import '../Styles/HinDetails.css';
import { useNavigate } from 'react-router-dom';

const HinDetails = ({ book }) => {
  const navigate = useNavigate();

  return (
    <div className="hin-details-container">
      <img src={book.image} alt={book.title} className="hin-image" />
      
      <div className="hin-info">
        <h2>{book.title}</h2>

        <div className="price-section">
          <span className="ori-price">Rs. {book.originalPrice}</span>
          <span className="dis-price">Rs. {book.discountPrice}</span>
        </div>

        <div className="stock">Only {book.stock} left in stock</div>
        <div className="binding">Binding: {book.binding}</div>
        <div className="condition">Condition: {book.condition}</div>

        <div className="buttons">
          <button className="add-cart">Add to cart</button>
          <button className="buy">Buy now</button>
        </div>

        <button className="back-button" onClick={() => navigate(-1)}>
          ‚Üê Back
        </button>
      </div>
    </div>
  );
};

export default HinDetails;
