import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import BookByCity from "./BookByCity";
import axios from "axios";
import "../Styles/Home.css";
import "./HeroCarousel.css"; // CSS for the custom carousel

const slides = [
   "https://www.1800flowers.com/blog/wp-content/uploads/2022/04/4576159-Gifting-Hardto-Herojpg.jpg",
  "https://houseofaroma.in/wp-content/uploads/2023/06/scented-candles-gift-set-2.webp",
  "https://www.epl.ca/wp-content/uploads/sites/18/2017/11/gift-ideas-for-book-lovers-850x560.jpg",
];

const Home = () => {
  const [searchCity, setSearchCity] = useState("");
  const [selectedCity, setSelectedCity] = useState("");
  const [book, setBook] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [currentSlide, setCurrentSlide] = useState(0);
  const navigate = useNavigate();

  // Carousel auto-rotate
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentSlide((prev) => (prev + 1) % slides.length);
    }, 4000);
    return () => clearInterval(interval);
  }, []);

  const handleSearch = async (e) => {
    e.preventDefault();
    const city = searchCity || selectedCity;
    if (!city) {
      setError("Please select or enter a city");
      return;
    }

    setLoading(true);
    setError(null);

    try {
      const response = await axios.get(`http://localhost:8080/cityResponse/${city}`);
      setBook(response.data);
      navigate(`/book/cityResponse/${city}`);
    } catch (err) {
      setError("Failed to fetch books.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="home-container">
      {/* Hero Section with Custom Carousel */}
      <div className="carousel-container">
        {slides.map((slide, index) => (
          <div
            key={index}
            className={`carousel-slide ${index === currentSlide ? "active" : ""}`}
            style={{ backgroundImage: `url(${slide})` }}
          >
            {index === currentSlide && (
              <div className="overlay-content">
                <h1>
                  Find the best <span>Kitabay</span> to purchase and read..
                </h1>
                <form onSubmit={handleSearch} className="search-form">
                  <BookByCity />
                  <input
                    type="text"
                    placeholder="Enter city name..."
                    value={searchCity}
                    onChange={(e) => setSearchCity(e.target.value)}
                  />
                  <button type="submit">Search</button>
                </form>
                {error && <p style={{ color: "red", marginTop: "10px" }}>{error}</p>}
              </div>
            )}
          </div>
        ))}
      </div>

      {/* Explore Categories */}
      <div className="explore-section">
        <h2>Explore Books by its type</h2>
        <div className="category-list">
          <Link to="/book/booktype/Best Sellers">Best Sellers</Link>
          <Link to="/book/booktype/New Releases">New Releases</Link>
          <Link to="/book/booktype/Spirituality">Spirituality</Link>
          <Link to="/book/booktype/Biographies">Biographies</Link>
        </div>
      </div>

      {/* Featured Books */}
      <div className="featured-section">
        <h2>Top Book Collections</h2>
        <div className="featured-list">
          <div className="featured-item">
            <img src="https://kitabay.com/cdn/shop/files/Copy_of_Decorate_your_home_with_Coffee_Table_Books_1.jpg?v=1727267160" alt="Book-1" />
            <p>Epic adventures to unknown pastures</p>
          </div>
          <div className="featured-item">
            <img src="https://kitabay.com/cdn/shop/files/Copy_of_Decorate_your_home_with_Coffee_Table_Books.jpg?v=1727267160" alt="Book-2" />
            <p>Dare to dream to believe</p>
          </div>
          <div className="featured-item">
            <img src="https://kitabay.com/cdn/shop/files/Copy_of_Decorate_your_home_with_Coffee_Table_Books_5.jpg?v=1738931005" alt="Book-3" />
            <p>Turn the page to new thrills</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Home;
