import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import "../Styles/Home.css";
import BookByCity from "./BookByCity";
import axios from "axios";

const Home = () => {
  const [searchCity, setSearchCity] = useState("");
  const [selectedCity, setSelectedCity] = useState("");
  const [book, setBook] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const navigate=useNavigate();

  const handleSearch = async (e) => {
    e.preventDefault();

    if (!searchCity && !selectedCity) {
      setError("Please select or enter a city");
      return;
    }

    setLoading(true);
    setError(null);
    const city = searchCity || selectedCity; // Use either the entered city or selected city

    try {
      const response = await axios.get(`http://localhost:8080/cityResponse/${city}`);
      console.log("response-->>>>",response);
      setBook(response.data); // Assuming the response contains the list of restaurants
      // Navigate to the results page with the city as a query parameter
      navigate(`/book/cityResponse/${city}`);
    } catch (err) {
      setError("Failed to fetch books.");
    } finally {
      setLoading(false);
    }
  };


  return (
    <div className="home-container">
  

      <div className="hero-section">
        <h1>Find the best <span>Kitabay</span> to purchase and read..</h1>
        <form onSubmit={handleSearch} className="search-form">
        <BookByCity/>
          <input
            type="text"
            placeholder="Enter city name..."
            value={searchCity}
            onChange={(e) => setSearchCity(e.target.value)}
          />
          <button type="submit">Search</button>
        </form>
      </div>

  
      <div className="featured-section">
        <h2>Top Book Collections</h2>
        <div className="featured-list">
          <div className="featured-item">
            <img src="https://kitabay.com/cdn/shop/files/Copy_of_Decorate_your_home_with_Coffee_Table_Books_1.jpg?v=1727267160" alt="Book-1" />
            
          </div>
          <div className="featured-item">
            <img src="https://kitabay.com/cdn/shop/files/Copy_of_Decorate_your_home_with_Coffee_Table_Books.jpg?v=1727267160" alt="Book-2" />
        
          </div>
          <div className="featured-item">
            <img src="https://kitabay.com/cdn/shop/files/Copy_of_Decorate_your_home_with_Coffee_Table_Books_5.jpg?v=1738931005" alt="Book-3" />
          
          </div>
        </div>
      </div>

      {/* Explore Categories */}
      <div className="explore-section">
        <h2>Good Reads</h2>
        <div className="category-list">
          <Link to="/book/booktype/Best Sellers">Best Sellers</Link><h1>|</h1>
          <Link to="/book/booktype/New Releases">New Release</Link><h1>|</h1>
          <Link to="/book/booktype/Spirituality">Spirituality</Link><h1>|</h1>
          <Link to="/book/booktype/Biographies">Biographies</Link><h1>|</h1>
          <Link to="/book/booktype/History">Historical</Link>
        </div>
      </div>

       <div className="book-card">
        <div className="book1">
          <img src="https://kitabay.com/cdn/shop/files/a478156d6328ab3cd0810bca5e4f0336.jpg?crop=center&height=283&v=1753077394&width=178" alt="Book-1"/>
          <Link to ="/book/1">The Young Elites</Link>
          <span>Rs.499 Rs.288</span>

        </div>

       </div>
      
    </div>
  );
};

export default Home;
