import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

const Mystery = () => {
  const { id } = useParams();
  const [books, setBooks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    axios
      .get(`http://localhost:8080/Mys/${id}`)
      .then((response) => {
        console.log("API Response:", response.data);
        const result = response.data?.Mys;
        if (result) {
          setBooks([result]); // wrap single object in array
        } else {
          setBooks([]);
        }
        setLoading(false);
      })
      .catch((error) => {
        console.error("Error fetching mystery books:", error);
        setError(error.message);
        setLoading(false);
      });
  }, [id]);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error: {error}</p>;

  return (
    <div>
      <h2>Mystery Book Details</h2>
      {books.length > 0 ? (
        <ul>
          {books.map((book) => (
            <li key={book.id}>
  <img src={book.image} alt={book.name} />
  <strong>{book.name}</strong> — ₹{book.price}
</li>

          ))}
        </ul>
      ) : (
        <p>No mystery books found.</p>
      )}
    </div>
  );
};

export default Mystery;
