import axios from "axios";

const API_BASE_URL = "http://localhost:8080";


export const fetchBook = async () => {
  try {
    const response = await axios.get(`${API_BASE_URL}/book`);
    return response.data;
  } catch (error) {
    console.error("Error fetching book:", error);
    return [];
  }
};

export const fetchBookDetails = async (id) => {
  try {
    const response = await axios.get(`${API_BASE_URL}/book/${id}`);
    return response.data.book;
  } catch (error) {
    console.error("Error fetching books:", error);
    return null;
  }
};


export const fetchBookByCity = async (city) => {
  try {
    const response = await axios.get(`${API_BASE_URL}/cityResponse/${city}`);
    return response.data;
  } catch (error) {
    console.error("Error fetching books in city:", error);
    return [];
  }
};

// User Signup
export const signupUser = async (userData) => {
  try {
    const response = await axios.post(`${API_BASE_URL}/signup`, userData);
    return response.data;
  } catch (error) {
    console.error("Signup failed:", error);
    return { error: "Signup failed" };
  }
};

// User Login
export const loginUser = async (email, password) => {
  try {
    const response = await axios.post("http://localhost:8080/login", {
      email,
      password,
    });
    return response.data; // ‚úÖ user data
  } catch (error) {
    // ‚ùå If backend sent 401 or any failure status
    const message =
      error.response?.data?.message || "Login failed. Check credentials.";
    throw message; // üö® throw so the Login page can catch and display it
  }
};
