import React , { useContext } from "react";
import { Link } from "react-router-dom";
import "../Styles/Navbar.css";
import { useAuth } from "../Context/AuthContext";

const { login, logout, isLoggedIn } = useAuth();

login(token);   // Call this after successful login
logout();       // Call this on logout button


const Navbar = () => {
   const { user, logout } = useContext(AuthContext);

   return(
    <nav className="navbar">
      <div className="logo">
        <Link to="/">Kitabay</Link>
      </div>

      <div className="opt">
        <Link to="/" className="opt-1">Home</Link>
        <Link to="/book" className="opt-2">Books</Link>
        {/* <Link to="/" className="opt-3">Mystery Box</Link>
        */}
        <Link to="/cart" className="nav-cart">
  🛒 Cart
</Link>
      </div>
      
      <div className="search-box">
        <input type="text" placeholder="🔍 Search for books..." />
      </div>

    
      <div className="nav-links">
        {user ? (
          <>
            <span className="username">👤 {user.name}</span>
            <button className="logout-btn" onClick={logout}>Logout</button>
          </>
        ) : (
          <>
            <Link to="/login" className="nav-button">Login</Link>
            <Link to="/signup" className="nav-button signup">Signup</Link>
          </>
        )}
      </div>
    </nav>

   );
};

export default Navbar;
