import { useParams } from "react-router-dom";
import { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

function Results() {
  const { city, booktype } = useParams();
  console.log("city, booktype -->>>", city, booktype);

  const [book, setBook] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchBook = async () => {
      try {
        let url = "";

        if (city) {
          url = `http://localhost:8080/cityResponse/${city}`;
        } else if (booktype) {
          url = `http://localhost:8080/bookType/${booktype}`;
        }

        if (url) {
          const response = await axios.get(url);
          console.log("Response data:", response.data);
          setBook(response.data.bookList || []);
        }
      } catch (err) {
        console.error("Fetch error:", err);
        setError("Failed to fetch books.");
      }
    };

    fetchBook();
  }, [city, booktype]);

  return (
    <div>
      <h1>
        Books in {city || booktype || "Our Collection"}
      </h1>

      {error && <p className="error">{error}</p>}

      <div className="book-list">
        {book.length > 0 ? (
          book.map((book) => (
            <div key={book._id} className="book-card">
              <Link to={`/book/${book._id}`} className="book-link">
                <img
                  src={book.image || "/default-book.jpg"}
                  alt={book.book}
                  className="book-image"
                />
                <div className="book-details">
                  <h2>{book.book}</h2>
                  <p>üìç {book.city}</p>
                </div>
              </Link>
            </div>
          ))
        ) : (
          <p>No books found.</p>
        )}
      </div>
    </div>
  );
}

export default Results;
