import React, { useEffect, useState } from "react";
import axios from "axios";
import '../Styles/Book.css';



const Book = () => {
  const [book, setBook] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    axios
      .get("http://localhost:8080/book")
      .then((response) => {
        console.log("API Response:", response.data); // Debugging
        if (Array.isArray(response.data)) {
          setBook(response.data);
        } else {
          console.error("Unexpected API response:", response.data);
          setBook([]); // Fallback to an empty array
        }
        setLoading(false);
      })
      .catch((error) => {
        console.error("Error fetching data:", error);
        setError(error.message);
        setLoading(false);
      });
  }, []);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error: {error}</p>;

  return (
    <div className="allbook">
      <h2>Books</h2>

      <ul>

           {Array.isArray(book) && book.length > 0 ? (
             book.map((book) => (
               <li key={book.id} className="css-id">
                <strong>
                <a href={`/book/${book.id}`}>{book.book}</a>
                </strong> - {book.city}

              <p>
                <a href={`/cityResponse/${book.city}`}>
                  See more books in {book.city}
                </a>
              </p>

            </li>




          ))
        ) : (
          <p>No books available.</p>
        )}
      </ul>
      </div>


  );
};

export default Book;
