import React, { useEffect, useState } from "react";
import axios from "axios";
import '../Styles/Hindi-coll.css';
import { useCart } from "../Context/CartContext";

const HindiColl = () => {
  const [coll, setHindiColl] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const { addToCart } = useCart();

  useEffect(() => {
    axios.get("http://localhost:8080/coll")
      .then((response) => {
        console.log("API Response:", response.data);
        if (Array.isArray(response.data)) {
          setHindiColl(response.data);
        } else {
          setHindiColl([]);
        }
        setLoading(false);
      })
      .catch((error) => {
        console.error("Error fetching data:", error);
        setError(error.message);
        setLoading(false);
      });
  }, []);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error: {error}</p>;
  
  const handleAddToCart = () => {
    const itemToAdd = {
      id: book.id || book._id,
      name: book.name,
      price: parseFloat(book.price),
      image: book.image,
    };
  
    addToCart(itemToAdd);
    alert("Book added to cart!");
  };
  return (
    <div className="allcoll">
      <h2>Hindi Book Collection</h2>
      <ul className="card-list">
        {coll.length > 0 ? (
          coll.map((book) => (
            <li key={book.id} className="css-coll">
              <img src={book.image} alt={book.name} />
              <strong>
                <a href={`/coll/${book.id}`}>{book.name}</a>
              </strong>
              <p className="price">Rs. {book.price}</p>
              <span className="original">Rs. 399</span>
              <button className="cart-btn" onClick={handleAddToCart}>Add to cart</button>
              <div className="stars">★★★★☆</div>
            </li>
          ))
        ) : (
          <p>No books available.</p>
        )}
      </ul>
    </div>
  );
};

export default HindiColl;
