import React, { useEffect, useState } from "react";
import axios from "axios";
import '../Styles/Hindi-coll.css';

const HindiColl = () => {
  const [coll, setHindiColl] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    axios.get("http://localhost:8080/coll")
      .then((response) => {
        console.log("API Response:", response.data);

        // Your API returns an array directly, so:
        if (Array.isArray(response.data)) {
          setHindiColl(response.data);
        } else {
          setHindiColl([]);
        }

        setLoading(false);
      })
      .catch((error) => {
        console.error("Error fetching data:", error);
        setError(error.message);
        setLoading(false);
      });
  }, []);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error: {error}</p>;

  return (
    <div className="allcoll">
      <h2>Hindi Books</h2>
      {Array.isArray(coll) && coll.length > 0 ? (
        coll.map((book) => (
         <li key={book.id} className="css-coll">
  <img src={book.image} alt={book.name} />
  <strong>
    <a href={`/coll/${book.id}`}>{book.name}</a>
  </strong>
  <p className="price">Rs. {book.price}</p>
  <span className="original">Rs. 399</span>
     
  
  <button className="add-to-cart">Add to cart</button>
  <div className="stars">★★★★☆</div> 
</li>

        ))
      ) : (
        <p>No books available.</p>
      )}
    </div>
  );
};

export default HindiColl;
