import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import "../Styles/Home.css";
import BookByCity from "./BookByCity";
import axios from "axios";

const Home = () => {
  const [searchCity, setSearchCity] = useState("");
  const [selectedCity, setSelectedCity] = useState("");
  const [book, setBook] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const navigate=useNavigate();

  const handleSearch = async (e) => {
    e.preventDefault();

    if (!searchCity && !selectedCity) {
      setError("Please select or enter a city");
      return;
    }

    setLoading(true);
    setError(null);
    const city = searchCity || selectedCity; // Use either the entered city or selected city

    try {
      const response = await axios.get(`http://localhost:8080/cityResponse/${city}`);
      console.log("response-->>>>",response);
      setBook(response.data); // Assuming the response contains the list of restaurants
      // Navigate to the results page with the city as a query parameter
      navigate(`/book/cityResponse/${city}`);
    } catch (err) {
      setError("Failed to fetch books.");
    } finally {
      setLoading(false);
    }
  };


  return (
    <div className="home-container">
      {/* Hero Section */}

      <div className="hero-section">
        <h1>Find the best books to purchase and read..</h1>
        <form onSubmit={handleSearch} className="search-form">
        <BookByCity/>
          <input
            type="text"
            placeholder="Enter city name..."
            value={searchCity}
            onChange={(e) => setSearchCity(e.target.value)}
          />
          <button type="submit">Search</button>
        </form>
      </div>

      {/* Explore Categories */}
      <div className="explore-section">
        <h2>Explore Books by its type</h2>
        <div className="category-list">
          <Link to="/book/booktype/Best Sellers">Best Sellers</Link>
          <Link to="/book/booktype/New Releases">New Release</Link>
          <Link to="/book/booktype/Spirituality">Spirituality</Link>
          <Link to="/book/booktype/Biographies">Biographies</Link>
        </div>
      </div>

      {/* Featured Restaurants */}
      <div className="featured-section">
        <h2>Top Book Collections</h2>
        <div className="featured-list">
          <div className="featured-item">
            <img src="https://www.bookswagon.com/images/promotionimages/web/childrenbooks3.jpg?v=7.0" alt="Book-1" />
            <p>Epic adventures to unknown pastures</p>
          </div>
          <div className="featured-item">
            <img src="https://www.bookswagon.com/images/promotionimages/web/childrenbooks1.jpg?v=7.0" alt="Book-2" />
            <p>Dare to dream to believe</p>
          </div>
          <div className="featured-item">
            <img src="https://www.bookswagon.com/images/promotionimages/web/fictionbooks2.jpg?v=7.0" alt="Book-3" />
            <p>Turn the page to new thrills </p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Home;
