import React from "react";
import { useNavigate } from "react-router-dom";
import "./HinDetails.css"; // Link to the CSS file

const HinDetails = ({ book }) => {
  const navigate = useNavigate();

  if (!book) return <div>Loading...</div>;

  return (
    <div className="book-details-container">
      <button className="back-btn" onClick={() => navigate(-1)}>‚Üê Back</button>

      <div className="book-details">
        <div className="book-image">
          <img src={book.image} alt={book.title} />
        </div>

        <div className="book-info">
          <h2>{book.title}</h2>

          <p>
            <del>Rs. {book.originalPrice}</del>{" "}
            <span className="price">Rs. {book.discountedPrice}</span>
          </p>

          <p>Only {book.stock} left in stock</p>
          <p>Binding: {book.binding}</p>
          <p>Condition: {book.condition}</p>

          <button className="cart-btn" disabled>Add to cart</button>
          <button className="buy-btn">Buy now</button>
        </div>
      </div>
    </div>
  );
};

export default HinDetails;
