import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
const navigate = useNavigate();
import "../Styles/HinDetails.css"; // style it like your second image

const HinDetails = () => {
  const { id } = useParams();
  const [book, setBook] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    axios.get(`http://localhost:8080/coll/${id}`)
      .then((res) => {
        setBook(res.data);
        setLoading(false);
      })
      .catch(() => {
        setBook(null);
        setLoading(false);
      });
  }, [id]);

  if (loading) return <p>Loading...</p>;
  if (!book) return <p>Book not found</p>;

  return (
    <div className="book-details-container">
  <button className="back-btn" onClick={() => navigate(-1)}>‚Üê Back</button>
  <div className="book-details">
    <div className="book-image">
      <img src={book.image} />
    </div>
    <div className="book-info">
      <h2>{book.title}</h2>
      <p><del>Rs. 499</del> <span className="price">Rs. 299</span></p>
      <p>Only 1 left in stock</p>
      <p>Binding: Paperback</p>
      <p>Condition: Gently Used</p>
      <button className="cart-btn">Add to cart</button>
      <button className="buy-btn">Buy now</button>
    </div>
  </div>
</div>
  );
};

export default HinDetails;
