import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import "../Styles/Home.css";
import candles from '../Assets/candles.jpg';
import { Swiper, SwiperSlide } from 'swiper/react';
import { Autoplay, Pagination } from 'swiper/modules';
import 'swiper/css';
import 'swiper/css/pagination';

const Home = () => {
  const [books, setBooks] = useState([]);

  useEffect(() => {
    axios.get("http://localhost:8080/Mys") // 🔁 update backend to support this if not yet
      .then(res => {
        setBooks(res.data.books); // your backend should send: { books: [...] }
      })
      .catch(err => {
        console.error("Failed to fetch books:", err.message);
      });
  }, []);

  return (
    <div className="home-container">

      {/* Carousel Section */}
      <Swiper
        modules={[Pagination, Autoplay]}
        pagination={{ clickable: true }}
        autoplay={{ delay: 3000 }}
        loop={true}
        className="hero-carousel"
      >
        <SwiperSlide>
          <Link to="/off">
            <img src="https://d34a0mln2492j4.cloudfront.net/unsigned/resize:fit:1872:455:0/gravity:sm/plain/https%3A%2F%2Fbookscape-s3-bucket.s3-ap-south-1.amazonaws.com%2F592EE252A9p_Sale_HP_hero_d.jpg" alt="Banner 1" className="hero-slide-img" />
          </Link>
        </SwiperSlide>
        <SwiperSlide>
          <Link to="/candleColl">
            <img src={candles} alt="Banner 2" className="hero-slide-img" />
          </Link>
        </SwiperSlide>
        <SwiperSlide>
          <Link to="/monColl">
            <img src="https://d34a0mln2492j4.cloudfront.net/unsigned/resize:fit:1872:455:0/gravity:sm/plain/https%3A%2F%2Fbookscape-s3-bucket.s3-ap-south-1.amazonaws.com%2F7423915D33ds_hero_banner_d.jpg" alt="Banner 3" className="hero-slide-img" />
          </Link>
        </SwiperSlide>
      </Swiper>

      {/* Featured Section */}
      <div className="featured-section">
        <h2>Top Book Collections</h2>
        <div className="featured-list">
          <div className="featured-item">
            <Link to="/coll"><img src="https://kitabay.com/cdn/shop/files/Copy_of_Decorate_your_home_with_Coffee_Table_Books.jpg?v=1727267160" alt="Book-1" /></Link>
          </div>
          <div className="featured-item">
            <Link to="/bundleColl"><img src="https://kitabay.com/cdn/shop/files/Copy_of_Decorate_your_home_with_Coffee_Table_Books_1.jpg?v=1727267160" alt="Book-2" /></Link>
          </div>
          <div className="featured-item">
            <Link to="/kidsColl"><img src="https://kitabay.com/cdn/shop/files/Copy_of_Decorate_your_home_with_Coffee_Table_Books_5.jpg?v=1738931005" alt="Book-3" /></Link>
          </div>
        </div>
      </div>

      {/* Category Header */}
      <div className="heading">
        <h2>Fiction Addiction</h2>
      </div>

      {/* Book Cards */}
      <div className="book-card">
        {books.map(book => (
          <div key={book._id} className="book">
            <Link to={`/Mys/${book._id}`}>
              <img src={book.image} alt={book.name} />
            </Link>
            <strong>{book.name}</strong>
            <p>
              <span className="original-price">Rs. {Math.floor(book.price * 1.2)}</span>
              <span className="discounted-price">Rs. {book.price}</span>
            </p>
            <span className="cart">Add to cart</span>
            <div className="sta">★★★★☆</div>
          </div>
        ))}
      </div>

      {/* Merch Section */}
      <div className="heading">
        <p>Not Just Books</p>
        <h2>We Have Merch Too!</h2>
      </div>

    </div>
  );
};

export default Home;
