import React, { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import axios from "axios";
import "../Styles/App.css";


const BookByCity = () => {
  const [cities, setCities] = useState([]);
  const [selectedCity, setSelectedCity] = useState("");
  const [book, setBook] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    // Fetch all restaurants to extract unique cities
    axios
      .get("http://localhost:8080/book")
      .then((response) => {
        if (response.data && Array.isArray(response.data)) {
          const uniqueCities = [
            ...new Set(response.data.map((r) => r.city)),
          ];
          setCities(uniqueCities);
        }
      })
      .catch((error) => console.error("Error fetching cities:", error));
  }, []);

  const handleCityChange = (event) => {
    const city = event.target.value;
    setSelectedCity(city);
    setLoading(true);
    setError(null);

    axios
      .get(`http://localhost:8080/cityResponse/${city}`)
      .then((response) => {
        if (response.data.cityResponse && Array.isArray(response.data.cityResponse)) {
          setBook(response.data.cityResponse);
          navigate(`/book/cityResponse/${city}`);
        } else {
          setBook([]);
        }
      })
      .catch((error) => {
        setError("Failed to fetch books.");
        setLoading(false);
      });
  };

  return (
    <div className="container">
      <h1 className="title">Book Store</h1>

      {/* Dropdown to select a city */}
      <div className="dropdown-container">
        <label>Select a City: </label>
        <select onChange={handleCityChange} value={selectedCity}>
          <option value="">-- Select --</option>
          {cities.map((city, index) => (
            <option key={index} value={city}>
              {city}
            </option>
          ))}
        </select>
      </div>

      {/* Loading & Error Messages */}
      {loading && <p className="loading">Loading books...</p>}
      {error && <p className="error">{error}</p>}

      {/* Restaurant List */}
      <div className="book-list">
        {book.length > 0 ? (
          book.map((book) => (
            <div key={book.id} className="book-card">
              {/* Link to restaurant details page */}
              <Link to={`/book/${book.id}`} className="book-link">
                <img
                  src={book.image || "/default-book.jpg"} // Default image if not provided
                  alt={book.restaurant}
                  className="book-image"
                />
                <div className="book-details">
                  <h2>{book.book}</h2>
                  <p>üìç {book.city}</p>
                </div>
              </Link>
            </div>
          ))
        ) : (
          <p className="no-data">No books found for this city.</p>
        )}
      </div>
    </div>
  );
};

export default BookByCity;;
