import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";
import "./App.css";

const BookDetails = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [book, setBook] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    // axios
    //   .get(`http://localhost:5400/restaurants/${id}`)
    //   .then((response) => {
    //     setRestaurant(response.data);
    //     setLoading(false);
    //   })
    //   .catch((error) => {
    //     setError("Failed to fetch restaurant details.");
    //     setLoading(false);
    //   });

    axios
  .get(`http://localhost:8080/book/${id}`)
  .then((response) => {
    setBook(response.data.book); // Ensure correct data extraction
    setLoading(false);
  })
  .catch(() => {
    setError("Failed to fetch book details.");
    setLoading(false);
  });

  }, [id]);

  if (loading) return <p className="loading">Loading...</p>;
  if (error) return <p className="error">{error}</p>;

  return (
    <div className="book-detail-container">
      <button onClick={() => navigate(-1)} className="back-button">‚¨Ö Go Back</button>

      <h1 className="book-title">{book.book}</h1>
      <img
        src={book.image || "/default-restaurant.jpg"}
        alt={book.book}
        className="book-detail-image"
      />
      <p className="book-city">üìç Location: {book.city}</p>
      <p className="book-description">
        üçΩÔ∏è A wonderful place for delicious food and a great ambiance.
      </p>
    </div>
  );
};

export default BookDetails;
